// Test script to upload images to ImgBB and get working URLs
import { uploadImageToImgBB } from './imgbbUpload';

// Sample base64 images for testing
const sampleImages = {
  coding: 'data:image/jpeg;base64,',
  award: 'data:image/jpeg;base64,',
  sports: 'data:image/jpeg;base64,',
  cultural: 'data:image/jpeg;base64,'
};

export const uploadTestImages = async () => {
  console.log('🚀 Starting test image uploads to ImgBB...');
  
  const results: { [key: string]: string } = {};
  
  try {
    for (const [name, base64Data] of Object.entries(sampleImages)) {
      console.log(`📤 Uploading ${name} image...`);
      
      try {
        const imageUrl = await uploadBase64ToImgBB(base64Data, `${name}.jpg`);
        results[name] = imageUrl;
        console.log(`✅ ${name} uploaded successfully:`, imageUrl);
      } catch (error) {
        console.error(`❌ Failed to upload ${name}:`, error);
      }
    }
    
    console.log('📋 Upload Results:', results);
    return results;
  } catch (error) {
    console.error('❌ Error in uploadTestImages:', error);
    return {};
  }
};

// Helper function to convert base64 to file
const uploadBase64ToImgBB = async (base64String: string, filename: string): Promise<string> => {
  try {
    // Convert base64 to file
    const byteCharacters = atob(base64String.split(',')[1]);
    const byteNumbers = new Array(byteCharacters.length);
    
    for (let i = 0; i < byteCharacters.length; i++) {
      byteNumbers[i] = byteCharacters.charCodeAt(i);
    }
    
    const byteArray = new Uint8Array(byteNumbers);
    const file = new File([byteArray], filename, { type: 'image/jpeg' });

    // Upload to ImgBB
    return await uploadImageToImgBB(file);
  } catch (error) {
    console.error('❌ Error converting base64 to ImgBB:', error);
    throw error;
  }
};

export default uploadTestImages; 